import React, { useState } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form';

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <Form.Control
    type="text"
    placeholder="Filtrar por local"
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
    ref={ref}
  />
));

const CustomMenu = React.forwardRef(({ children, style, className }, ref) => {
  const [value, setValue] = useState('');

  return (
    <div ref={ref} style={style} className={className}>
      <Form.Control
        autoFocus
        className="mx-3 my-2 w-auto"
        placeholder="Type to filter..."
        onChange={(e) => setValue(e.target.value)}
        value={value}
      />
      <ul className="list-unstyled">
        {React.Children.toArray(children).filter(
          (child) =>
            !value || child.props.children.toLowerCase().startsWith(value.toLowerCase())
        )}
      </ul>
    </div>
  );
});

const BuscaLocal = () => {
  return (
    <Dropdown>
      <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components" />
      
        <Dropdown.Item eventKey="1">SÃ£o Paulo</Dropdown.Item>
        <Dropdown.Item eventKey="2">Rio de Janeiro</Dropdown.Item>
        <Dropdown.Item eventKey="3">Santos</Dropdown.Item>
        <Dropdown.Item eventKey="4">Curitiba</Dropdown.Item>
      
    </Dropdown>
  );
};

export default BuscaLocal;
