import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';

const BuscaLocal = () => {
  const [value, setValue] = useState('');
  const [open, setOpen] = useState(false);

  const handleInputChange = (e) => {
    setValue(e.target.value);
    setOpen(true);
  };

  return (
    <Dropdown show={open}>
      <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
        {value ? value : 'Filtrar por local'}
      </Dropdown.Toggle>
      <Dropdown.Menu as={CustomMenu}>
        <Form.Control
          autoFocus
          className="mx-3 my-2 w-auto"
          placeholder="Type to filter..."
          onChange={handleInputChange}
          value={value}
        />
        <Dropdown.Item eventKey="1" active>SÃ£o Paulo</Dropdown.Item>
        <Dropdown.Item eventKey="2">Rio de Janeiro</Dropdown.Item>
        <Dropdown.Item eventKey="3">Santos</Dropdown.Item>
        <Dropdown.Item eventKey="4">Curitiba</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
};

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
  </a>
));

const CustomMenu = React.forwardRef(({ children, style, className }, ref) => (
  <div ref={ref} style={style} className={className}>
    {React.Children.toArray(children)}
  </div>
));

export default BuscaLocal;
