import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { format } from 'date-fns';
import Card from 'react-bootstrap/Card';

const Jobs = () => {
  const [vagas, setVagas] = useState([]);

  useEffect(() => {
    const fetchVagas = async () => {
      try {
        const response = await axios.get('http://localhost:3000/');
        const vagasComRequisitos = await Promise.all(response.data.map(async (vaga) => {
          const requisitos = await vagaRequisitos(vaga.id);
          return { ...vaga, requisitos };
        }));
        setVagas(vagasComRequisitos);
      } catch (error) {
        console.error('Erro ao buscar vagas:', error);
      }
    };

    fetchVagas();
  }, []);

  const vagaRequisitos = async (idreq) => {
    try {
      const response = await axios.get(`http://localhost:3000/requisitos/:id${idreq}`);
      return response.data;
    } catch (error) {
      console.error('Erro ao buscar requisitos:', error);
      throw error;
    }
  };

  return (
    <>
      {vagas.map((vaga, index) => (
        <Card style={{ width: '22rem', height: '18rem' }} key={index}>
          <Card.Body>
            <Card.Title>
              {' '}
              <img src="https://via.placeholder.com/50" alt="" /> {vaga.nome_empresa}
            </Card.Title>
            <Card.Title> {vaga.cargo}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">
              {format(new Date(vaga.data_pub), 'dd-MM-yyyy')} - {vaga.localidade}
            </Card.Subtitle>
            <Card.Text>
              <ul>
                {vaga.requisitos.map((requisito, idx) => (
                  <li key={idx}>{requisito}</li>
                ))}
              </ul>
            </Card.Text>
          </Card.Body>
        </Card>
      ))}
    </>
  );
};

export default Jobs;
