import React, { useState } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import Form from 'react-bootstrap/Form';

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <Form.Control
    type="text"
    placeholder="Filtrar por local"
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
    ref={ref}
  />
));

const CustomMenu = React.forwardRef(({ children, style, className }, ref) => {
  // Este trecho de código foi removido
  // const [value, setValue] = useState('');

  return (
    <div ref={ref} style={style} className={className}>
      {/* O input de filtro foi removido daqui */}
      <ul className="list-unstyled">
        {React.Children.toArray(children)}
      </ul>
    </div>
  );
});

const BuscaLocal = () => {
  // Estado para controlar o valor do input de filtro
  const [filterValue, setFilterValue] = useState('');

  // Função para filtrar as opções com base no valor digitado
  const filterOptions = (options) => {
    return options.filter(option =>
      option.props.children.toLowerCase().startsWith(filterValue.toLowerCase())
    );
  };

  return (
    <Dropdown>
      <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components" />
      <Dropdown.Menu as={CustomMenu}>
        <Form.Control
          autoFocus
          className="mx-3 my-2 w-auto"
          placeholder="Type to filter..."
          onChange={(e) => setFilterValue(e.target.value)}
          value={filterValue}
        />
        {/* As opções são filtradas com base no valor do input de filtro */}
        {filterOptions([
          <Dropdown.Item key="1">São Paulo</Dropdown.Item>,
          <Dropdown.Item key="2">Rio de Janeiro</Dropdown.Item>,
          <Dropdown.Item key="3">Santos</Dropdown.Item>,
          <Dropdown.Item key="4">Curitiba</Dropdown.Item>
        ])}
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default BuscaLocal;
