import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { format, setWeek } from 'date-fns'
import Button from 'react-bootstrap/Button'
import ListaRequisitos from './ListaRequisitos'

const Apply = (id) => {


    const [vaga, setVaga] = useState() 


    const buscarVaga = async (id) =>{
        try{
            const response = await axios.get(`http://localhost:3000/vagas_dev/${id}`)
            setVaga(response.data)
            
        }catch(error){
            console.error('Erro ao buscar vaga:', error)
        }
    }

useEffect(() =>{
    buscarVaga(3)
}, [])

    return(
        <>
        {vaga && (
    <>
            <header>
               <img src="https://via.placeholder.com/80" alt="" /> 
               <h1>{vaga.nome_empresa}</h1>
               <h2> <a href="https://portifolio-seven-wine.vercel.app/content/index.html" target='_blank'>{vaga.nome_empresa}.com</a> </h2>
               <Button>Aplicar para a vaga</Button>
            </header>
            
            <article>
                <h2>{format(new Date(vaga.data_pub), 'dd-MM-yyyy')}</h2>
                <h1>{vaga.cargo}</h1>
                <h2>{vaga.localidade}</h2>

                <p>{vaga.descricao}</p>

            </article> 
    </>
        )}
        </>
    )
    
}

export default Apply
