import Jobs from './Jobs'
import './Home.css'
import { Container, Row, Col } from 'react-bootstrap';

const Home = () => {

  
    // Divide as vagas em grupos de até 3
    const vagasPorLinha = vagas.reduce((acc, curr, index) => {
        const linhaAtual = Math.floor(index / 3);
        acc[linhaAtual] = acc[linhaAtual] || [];
        acc[linhaAtual].push(curr);
        return acc;
    }, []);

    return (
        <Container>
            {vagasPorLinha.map((grupo, index) => (
                <Row key={index}>
                    {grupo.map((vaga, idx) => (
                        <Col key={idx} xs={12} sm={6} md={4} lg={4} xl={4}>
                            {/* Aqui você renderiza o componente que representa cada vaga */}
                            <Jobs vaga={vaga} />
                        </Col>
                    ))}
                </Row>
            ))}
        </Container>
    );
}
export default Home
