import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { format } from 'date-fns'
import Card from 'react-bootstrap/Card'
import ReadMoreReact from 'read-more-react';

const Jobs = () => {

const [vagas, setVagas] = useState([])

useEffect(() => {
    const fetchVagas = async () =>{
        try{
            const response = await axios.get('http://localhost:3000/')
            setVagas(response.data)
        }
        catch(error){
            console.error('Erro ao buscar vagas:', error)
        }
    }

    fetchVagas()
}, [])

   const vagaRequisitos = async(idreq) => {
    try{
        const response = await axios.get(`http://localhost:3000/requisitos/:id${idreq}`)
        return response.data
    } catch(error){
        console.error('Erro ao buscar')
    }
    
   }
    return(
        <>
             {
             
             vagas.map((vaga, index) => (

                <Card style={{ width: '22rem', height: '18rem'}} key={index}>
                <Card.Body>
                    <Card.Title> <img src="https://via.placeholder.com/50" alt="" /> {vaga.nome_empresa}</Card.Title>
                    <Card.Title> {vaga.cargo}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{format(new Date(vaga.data_pub), 'dd-MM-yyyy')} - {vaga.localidade}</Card.Subtitle>
                    <Card.Text>
                        {vagaRequisitos(index)}

                        <ReadMoreReact text={vaga.descricao} 
                        min={100}
                        ideal={100}
                        max={800}
                        readMoreText={
                        <Card.Link>Ver mais</Card.Link>
                    }
                        ></ReadMoreReact>
                        
                        
                        </Card.Text> 
                    
                </Card.Body>
                </Card>
                
                
             ))}
                
                
             

            
        </>
    )
}

export default Jobs
